mapper.delete:
	 delete from xf_syn_atachment
	  WHERE 1=1
      and matter_id=#{matterId}

mapper.insert:
INSERT INTO xf_syn_atachment ( id, form_id, matter_id, filename, files, create_time, is_handle ) SELECT xf110.Nextval as ID,A.* from(
select #{item.formId,jdbcType=VARCHAR} , #{item.matterId,jdbcType=INTEGER} , #{item.filename,jdbcType=VARCHAR} , #{item.files,jdbcType=BLOB} , #{item.createTime,jdbcType=TIMESTAMP} , #{item.isHandle,jdbcType=INTEGER} from dual
) A
